import React, { useState } from "react";
import { Card, Grid, Typography } from 'hero-design'
import { Image } from "react-bootstrap";
import DocumentPanel from "../documentPanel";

export default function DocumentCard({ cardInfor }: { cardInfor: any }) {

    const [openDetail, setOpenDetail] = useState<boolean>(false);
    function openDetailPopup(e: any) {
        setOpenDetail(true)
    }

    const [mouseHover, setMouseHover] = useState<boolean>(false);
    function onMouseHover(e: any) {
        setMouseHover(true)
    }
    function onMouseLeave(e: any) {
        setMouseHover(true)
    }

    return (
        <>
            <Card
                key={cardInfor.id}
                id={cardInfor.id}
                variant={'primary'}
                style={{ width: '15rem', margin: '10px' }}
                imageSrc={cardInfor.path}
            >
                <div onMouseEnter={onMouseHover}
                    onMouseLeave={onMouseLeave}
                    onClick={openDetailPopup}
                    style={{ cursor: "pointer" }}>

                    <Card.Header style={{
                        wordWrap: 'break-word',
                        margin: '10px',
                        overflow: "hidden",
                        textOverflow: 'ellipsis',
                        whiteSpace: 'nowrap'
                    }}>
                        {cardInfor.title}
                    </Card.Header>
                    <Card.Content style={{ wordWrap: 'break-word' }}>
                        <div style={{ position:'relative'}}>
                            <Image
                                src={cardInfor.path}
                                fluid
                                style={{ width: '100%', height: '10rem' }}
                            />
                            {mouseHover &&
                                <div style={{
                                            position: 'absolute',
                                            backgroundColor:"antiquewhite",
                                            top: '85%',
                                            left: '0px',
                                            right: 0,
                                            bottom: 0,
                                        }}
                                >
                                    <Image src="./shoppingCart.png" 
                                        style={{width:'1rem', 
                                                height:'1rem',
                                                float:'left'}}
                                    />

                                    <Typography.Text style={{ fontSize:'1rem' }}>
                                        Add to cart
                                    </Typography.Text>
                                </div>
                            }
                        </div>

                    </Card.Content>

                </div>

            </Card>

            {openDetail &&
                (<DocumentPanel onClose={() => setOpenDetail(false)} docId={cardInfor.id} />)
            }
        </>
    )
}
