import { Button, Grid, Input, Switch } from "hero-design";
import { useState } from "react";

export default function DocumentDetails({ doc }: { doc: any }) {
    const [editMode, setEditMode] = useState(false);

    const detailsInput = ['Title']
    const detailsInputElements = detailsInput.map(key => {
        return (
            <Grid.Row>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    {key}
                </Grid.Col>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    <Input disabled={!editMode} name={key} defaultValue={doc[key.toLowerCase()]}>
                    </Input>
                </Grid.Col>
            </Grid.Row>
        )
    })

    const detailsSwitch = ['Publish']
    const detailsSwitchElements = detailsSwitch.map(key => {
        return (
            <Grid.Row>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    {key}
                </Grid.Col>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    <Switch
                        icon="checkmark"
                        checked={doc[key.toLowerCase()]}
                        name={key}
                    />   
                </Grid.Col>
            </Grid.Row>
        )
    })

    const detailsInputNumber = ['Price']
    const detailsInputNumberElements = detailsInputNumber.map(key => {
        return (
            <Grid.Row>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    {key}
                </Grid.Col>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    <Input
                        onKeyPress={(event) => {
                            const regex = /[0-9]/
                            if (!regex.test(event.key)) {
                                event.preventDefault()
                            }
                        }}
                        disabled={!editMode}
                        name={key}
                        defaultValue={doc[key.toLowerCase()]}>
                    </Input>
                </Grid.Col>
            </Grid.Row>
        )
    })

    const detailsAreaText = ['Description']
    const detailsAreaTextElements = detailsAreaText.map(key => {
        return (
            <Grid.Row>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    {key}
                </Grid.Col>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    <Input.TextArea
                        disabled={!editMode}
                        name={key}
                        defaultValue={doc[key.toLowerCase()]}>
                    </Input.TextArea>
                </Grid.Col>
            </Grid.Row>
        )
    })

    function save() {

        }

    return (
        <form onSubmit={save}>
            <Grid>
                {detailsInputElements}
                {detailsAreaTextElements}
                {detailsInputNumberElements}
                {detailsSwitchElements}
                <Grid.Row>
                    <Button onClick={(e) => setEditMode(true)} disabled={!editMode} text={"Edit"} />
                    <Button type={'submit'} text={"Save"} />
                </Grid.Row>
            </Grid>
        </form>
    )

}
