import { Divider, Grid } from 'hero-design';
import fileDownload from 'js-file-download';
import { isEmpty } from 'lodash';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { toast, ToastContainer } from 'react-toastify';
import Header from '../shared/components/header';
import { getOrders } from './apiCalls/list';
import { makeOrder } from './apiCalls/makeOrder';
import OrderCard from './components/OrderCard';

export default function Histories() {
    const [orders, setOrders] = useState<any[]>([]);
    const user = useSelector((state: any) => state.user)

    const orderCards = orders.map(item => {
        return <OrderCard key={item.line_item_id} item={item}/>
    })
    useEffect(() => {
        getOrders(user.token, user.id)
            .then(res => {
                if (res.status == 200) {
                    if (Array.isArray(res.data)) {
                        const dataDeepCopy = res.data.map(doc => Object.assign({}, doc))
                        setOrders(dataDeepCopy)
                    }
                }
            })
    }, [refresh, orders])

    return (
        <>
            <Header />
            <Divider />
            <Grid>
                <Grid.Row
                    style={{
                        flexDirection: 'column',
                        alignItems: 'center'
                    }}
                >
                    {orderCards}
                </Grid.Row>
            </Grid>
            <ToastContainer />
        </>
    )
}
