import { Modal, Tabs } from 'hero-design';
import React, { useState } from 'react';
import DocumentOverView from '../documentOverview';

export default function DocumentPanel({ doc, onClose }: { doc: any, onClose:()=>void }) {

    const [selectedTabId, setSelectedTabId] = useState(1);

    const tabs = [
        {id: 1, title: 'Overview', panel: <DocumentOverView doc={doc}/>},
        {id: 2, title: 'Details', panel: 'Details'},
    ]

    return (
        <Modal
            open
            variant={'basic'}
            title={doc.title}
            onClose={onClose}
            size={'xlarge'}
        >
           <Tabs
            id={'document-panel-tab'}
            tabs={tabs}
            selectedTabId={selectedTabId}
            onChange={newTabId => setSelectedTabId(newTabId as number)}
           /> 
        </Modal>
    )
}
