import { Image } from "react-bootstrap";

export default function LineItemCard({ item }: { item: any }) {
    console.log(item)
    return (
        <Grid>
            <Grid.Row
                style={{
                    border: 'solid',
                    borderRadius: '8px',
                    marginTop: '1rem',
                    marginLeft: '2rem',
                    height: '7rem',
                    width: '60rem',
                }}
            >
                <Grid.Col span={[4, 4, 4, 4, 4]} style={{
                    display:'flex',
                    justifyContent:'center',
                }}>
                    <Box>
                        <Image
                            src={item.path}
                            roundedCircle
                            style={{
                                top: 0,
                                left: 0,
                                width: '5rem',
                                height: '5rem',
                                padding: '1rem'
                            }}
                        />
                    </Box>

                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[8, 8, 8, 8, 8]}>

                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[8, 8, 8, 8, 8]}></Grid.Col>
            </Grid.Row>
        </Grid>
    )
}
