        {id: 1, title: 'Overview', panel: <DocumentOverView doc={doc}/>},
        {id: 2, title: 'Details', panel: <DocumentDetails doc={doc}/>},
    ]

    return (
        <Modal
            open
            variant={'basic'}
            title={doc.title}
            onClose={onClose}
            size={'xlarge'}
        >
           <Tabs
            id={'document-panel-tab'}
            tabs={tabs}
            selectedTabId={selectedTabId}
            onChange={newTabId => setSelectedTabId(newTabId as number)}
           /> 
        </Modal>
    )
}
