const styles = {
    navLink: {
        paddingLeft: 13,
        textDecoration: 'none',
        color: 'black'
    },
}

export default function Header() {
    const history = useHistory()
    const userId = useSelector((state: any) => state.user.id)

    const [open, setOpen] = useState(false)
    const closeDropdown = () => setOpen(false)
    const dropdownContent = (
