                    style={{
                        width: '100px',
                        height: '',
                        paddingLeft: '20px',
                        cursor: 'pointer'
                    }}
                    onClick={(e: any) => {
                        history.push('/dam')
                    }}
                ></Image>
            </Grid.Col>

            <Grid.Col span={[8, 8, 8, 8, 8]}></Grid.Col>
            <Grid.Col span={[8, 8, 8, 8, 8]} style={{}}>
                <NavLink
                    to="/marketplace"
                    activeStyle={{
                        fontWeight: "bold",
                        color: "black"
                    }}
                    style={styles.navLink}
                >
                    Marketplace
                </NavLink>
                <NavLink
                    to="/orders"
                    activeStyle={{
                        fontWeight: "bold",
                        color: "black"
                    }}
                    style={styles.navLink}
                >
                    Orders
                </NavLink>
                <Dropdown
                    open={open}
                    content={dropdownContent}
                    target={
                        <Button
                            text="Account"
                            rightIcon="arrow-down"
                            onClick={() => setOpen(!open)}
                            style={{
                                background:'white',
                                color: "black",
                                fontSize: '1rem'
                            }}

                        />
