import { Button, Grid, Input, PageHeader, Typography } from 'hero-design';
import React, { useRef, useState } from 'react'
import { useDispatch } from 'react-redux';
import { useTheme } from 'styled-components';
import { loginAsyc } from './apiCalls/login';
import { loginAction } from './reducers/reducers';


function Login() {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();

    function login()
    {
        console.log(userName, password)
        loginAsyc(userName, password)
        .then((data:any)=>{
            if (data.constructor == Object)
            {
                dispatch(loginAction({
                    token: data.auth_token,
                    username: userName
                }))
            }
        })
    }

    return (
        <>
            <Grid>
                <Grid.Row style={{ margin: `10px` }}>
                    <Grid.Col span={[16, 16, 16, 16, 16]} offset={[4, 4, 4, 4, 4]}>
                        <PageHeader title="August digital asset management" />
                    </Grid.Col>
                </Grid.Row>
                <Grid.Row style={{ margin: `10px` }}>
                    <Grid.Col span={[16, 16, 16, 16, 16]} offset={[4, 4, 4, 4, 4]}>
                        <Input
                            onChange={(e) => {setUserName(e.target.value.trim())}}
                            placeholder="Please enter your user name"
                            size="large"
                            style={{ margin: `10px` }}
                        />
                        <Input
                            onChange={(e) => {setPassword(e.target.value.trim())}}
                            placeholder="Please enter your password"
                            size="large"
                            style={{ margin: `10px` }}
                        />
                    </Grid.Col>
                </Grid.Row>
                <Grid.Row style={{ margin: `10px` }}>
                    <Grid.Col span={[4, 4, 4, 4, 4]} offset={[18, 18, 18, 18, 18]}>
                        <Button
                            onClick={login}
                            variant="filled"
                            intent="success"
                            text="Login"
                            size="large">
                        </Button>
                    </Grid.Col>

                </Grid.Row>
            </Grid>


        </>

    )
}

export default Login;
