import { Types } from "./actionTypes"
import produce from "immer"


const initialState = {
    user: {
        username: '',
        password: '',
        token: '',
    }
}

const reducer = (state = initialState, action: { type: String, payload: any }) => {
    produce(state, (draft) => {
        switch (action.type) {
            case Types.LOGIN:
                {
                    draft.user = action.payload.user
                }
                break;
        }
    })

}

export default reducer;
