import { Types } from "./actionTypes"
import produce from "immer"

const initialState = {
    user: {
        username: '',
        password: '',
        token: '',
    }
}

const reducer = (state = initialState, action: { type: String, payload: any }) => {
    switch (action.type) {
        case Types.LOGIN:
            return  {
                ...state,
                user: action.payload.user
            }
    }
    return state;
}

export default reducer;
