import React, { useState } from "react";
import { Card, Grid, Typography } from 'hero-design'
import { Image } from "react-bootstrap";
import DocumentPanel from "../documentPanel";

export default function DocumentCard({ cardInfor }: { cardInfor: any }) {

    const [openDetail, setOpenDetail] = useState<boolean>(false);
    function openDetailPopup(e: any) {
        setOpenDetail(true)
    }

    const [mouseHover, setMouseHover] = useState<boolean>(false);
    function onMouseHover(e: any) {
        setMouseHover(true)
    }
    function onMouseLeave(e: any) {
        setMouseHover(false)
    }

    return (
        // <div onMouseEnter={onMouseHover} onMouseLeave={onMouseLeave}>
        <>
            <Card
                key={cardInfor.id}
                id={cardInfor.id}
                variant={'primary'}
                style={{ width: '15rem', margin: '10px' }}
                imageSrc={cardInfor.path}
            >
                <div onClick={openDetailPopup} style={{ cursor: "pointer" }}>
                    <Card.Header style={{
                        wordWrap: 'break-word',
                        margin: '10px',
                        overflow: "hidden",
                        textOverflow: 'ellipsis',
                        whiteSpace: 'nowrap'
                    }}>
                        {cardInfor.title}
                    </Card.Header>
                    <Card.Content style={{ wordWrap: 'break-word' }}>
                        <div>
                            <Image
                                src={cardInfor.path}
                                fluid
                                style={{ width: '100%', height: '10rem' }}
                            />
                            {mouseHover &&
                                <Typography.Text>Add to cart</Typography.Text>
                            }
                        </div>

                    </Card.Content>

                </div>

            </Card>

            {openDetail &&
                (<DocumentPanel onClose={() => setOpenDetail(false)} docId={cardInfor.id} />)
            }
            </>
        // </div>
    )
}
