import { Box, Button, Divider, Grid, Icon, Input, Typography } from "hero-design";
import { useState } from "react";
import { Image } from "react-bootstrap";
import { useSelector } from "react-redux";
import { toast } from "react-toastify";
import { setQuantityForLineItem } from "../../apiCalls/LineItemCard/changeQuantity";

const buttonStyle = ({ hover }: { hover: any }) => ({
    cursor: 'pointer',
})

export default function LineItemCard({ item }: { item: any }) {
    const token = useSelector((state: any) => state.user.token)

    const inforKeys = ['Price', 'File_Name', 'Media_Type']
    const inforElements = inforKeys.map(infoKey => {
        return (
            <Grid.Row key={infoKey}>
                <Grid.Col span={[12, 12, 12, 12, 12]} offset={[2, 2, 2, 2, 2]}>
                    {infoKey}
                </Grid.Col>
                <Grid.Col span={[10, 10, 10, 10, 10]}>
                    {
                        <Typography.Text style={{ wordWrap: 'break-word' }}>
                            {`${item[infoKey.toLowerCase()]}`}
                        </Typography.Text>
                    }
                </Grid.Col>
            </Grid.Row>
        )
    })

    const [quantity, setQuantity] = useState<number>(item.line_item_quantity);
    function setQuantityWith(number: number) {
        setQuantityForLineItem(token, item.line_item_id, number)
            .then((res => {
                console.log(res)
                if (res.status === 200) {
                    setQuantity(number)
                }
            }))
    }

    const [hover, setHover] = useState(false);

    return (
        <Grid>
            <Grid.Row
                style={{
                    border: 'solid',
                    borderRadius: '8px',
                    marginTop: '1rem',
                    marginLeft: '2rem',
                    height: '7rem',
                    width: '60rem',
                }}
            >
                <Grid.Col span={[4, 4, 4, 4, 4]} style={{
                    display: 'flex',
                    justifyContent: 'center',
                }}>
                    <Box>
                        <Image
                            src={item.path}
                            roundedCircle
                            style={{
                                top: 0,
                                left: 0,
                                width: '5rem',
                                height: '5rem',
                                padding: '1rem'
                            }}
                        />
                    </Box>

                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[8, 8, 8, 8, 8]}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                    }}
                >
                    {inforElements}
                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[4, 4, 4, 4, 4]}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Typography.Text
                        style={{
                            margin: '15px'
                        }}
                    >
                        Quantity
                    </Typography.Text>
                    <Grid.Row
                        style={{
                            justifyContent: 'space-between'
                        }}
                    >
                        <Grid.Col span={[4, 4, 4, 4, 4]}
                            style={{
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'center',
                            }}
                        >
                            <div
                                onClick={() => setQuantityWith(quantity - 1)}
                                style={buttonStyle({ hover })}
                                onMouseEnter={() => setHover(true)}
                                onMouseLeave={() => setHover(false)}
                            >
                                <Icon
                                    style={{
                                        width: '16px',
                                        height: '16px',
                                    }}
                                    icon={
                                        <img src="data:image/png;base64," />
                                    }
                                ></Icon>
                            </div>

                        </Grid.Col>
                        <Grid.Col span={[10, 10, 10, 10, 10]}>
                            <Input
                                style={{
                                    textAlign: 'center'
                                }}
                                onChange={(event:any)=>{
                                    const regex = /^\d*$/
                                    if (!regex.test(event.target.value)) {
                                        event.preventDefault()
                                    }
                                    else {
                                        const value = event.target.value.trim() || 0
                                        setQuantityWith(parseInt(value))
                                    }
                                }}
                                value={quantity.toString()}
                            >
                            </Input>
                        </Grid.Col>
                        <Grid.Col span={[4, 4, 4, 4, 4]}
                            style={{
                                display: 'flex',
                                justifyContent: 'center',
                            }}
                        >
                            <div
                                onClick={() => setQuantityWith(quantity + 1)}
                                style={buttonStyle({ hover })}
                                onMouseEnter={() => setHover(true)}
                                onMouseLeave={() => setHover(false)}
                            >
                                <Icon
                                    style={{
                                        width: '16px',
                                        height: '16px',
                                    }}
                                    icon={
                                        <img src="data:image/png;base64," />
                                    }
                                ></Icon>
                            </div>
                        </Grid.Col>
                    </Grid.Row>
                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[4, 4, 4, 4, 4]}></Grid.Col>
            </Grid.Row>
        </Grid>
    )
}
