import { Button, Divider, Grid, Icon, Input, Typography } from "hero-design";
import { useEffect, useState } from "react";
import { Image } from "react-bootstrap";
import { useSelector } from "react-redux";
import DocumentPanel from "../../../Marketplace/components/documentPanel";
import { setQuantityForLineItem } from "../../apiCalls/LineItemCard/changeQuantity";

export default function OrderCard({ order }: { order: any }) {
    const token = useSelector((state: any) => state.user.token)

    const inforKeys = ['Name', 'Address', 'Email', 'Total_price']
    const inforElements = inforKeys.map(infoKey => {
        return (
            <Grid.Row key={infoKey}>
                <Grid.Col span={[12, 12, 12, 12, 12]} offset={[2, 2, 2, 2, 2]}>
                    {infoKey}
                </Grid.Col>
                <Grid.Col span={[10, 10, 10, 10, 10]}>
                    {
                        <Typography.Text style={{ wordWrap: 'break-word' }}>
                            {`${order[infoKey.toLowerCase()]}`}
                        </Typography.Text>
                    }
                </Grid.Col>
            </Grid.Row>
        )
    })


    return (
            <Grid.Row
                style={{
                    border: 'solid',
                    borderRadius: '8px',
                    marginTop: '1rem',
                    height: '7rem',
                    width: '80%',
                }}
            >
                <Grid.Col span={[4, 4, 4, 4, 4]}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                    }}
                >
                    <Typography.Text>
                        No.
                    </Typography.Text>
                    {order.id}
                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[16, 16, 16, 16, 16]}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                    }}
                >
                    {inforElements}
                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[4, 4, 4, 4, 4]}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                    }}
                >
                    <Button text="Download"></Button>
                </Grid.Col>

            </Grid.Row>
    )
}
