import { Box, Divider, Grid, Typography } from "hero-design";
import { Image } from "react-bootstrap";

export default function LineItemCard({ item }: { item: any }) {

    const inforKeys = ['Price', 'File_Name', 'Media_Type']
    const inforElements = inforKeys.map(infoKey => {
        return (
            <Grid.Row key={infoKey}>
                <Grid.Col span={[12, 12, 12, 12, 12]} offset={[2, 2, 2, 2, 2]}>
                    {infoKey}
                </Grid.Col>
                <Grid.Col span={[10, 10, 10, 10, 10]}>
                    {
                        <Typography.Text style={{ wordWrap: 'break-word' }}>
                            {`${item[infoKey.toLowerCase()]}`}
                        </Typography.Text>
                    }
                </Grid.Col>
            </Grid.Row>
        )
    })
    return (
        <Grid>
            <Grid.Row
                style={{
                    border: 'solid',
                    borderRadius: '8px',
                    marginTop: '1rem',
                    marginLeft: '2rem',
                    height: '7rem',
                    width: '60rem',
                }}
            >
                <Grid.Col span={[4, 4, 4, 4, 4]} style={{
                    display: 'flex',
                    justifyContent: 'center',
                }}>
                    <Box>
                        <Image
                            src={item.path}
                            roundedCircle
                            style={{
                                top: 0,
                                left: 0,
                                width: '5rem',
                                height: '5rem',
                                padding: '1rem'
                            }}
                        />
                    </Box>

                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[8, 8, 8, 8, 8]}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                    }}
                >
                    {inforElements}
                </Grid.Col>
                <Divider variant="vertical" />
                <Grid.Col span={[8, 8, 8, 8, 8]}></Grid.Col>
            </Grid.Row>
        </Grid>
    )
}
