import { Button } from 'hero-design';
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { toast, ToastContainer } from 'react-toastify';
import { getDocsAsync } from './apiCalls/list';
import DocumentCard from './components/documentCard';
import { injectStyle } from "react-toastify/dist/inject-style";

if (typeof window !== 'undefined')
{
    injectStyle()
}

export default function Dam() {
    const [docs, setDocs] = useState<any[]>([]);
    const token = useSelector((state: any) => state.user.token)

    useEffect(() => {
        getDocsAsync(token)
            .then(res => {
                if (res.status == 200) {
                    if (Array.isArray(res.data)) {
                        const dataDeepCopy = res.data.map(doc => Object.assign({}, doc))
                        setDocs(dataDeepCopy)
                    }
                }
            })
    }, [])

    const docCards = docs.map(doc => {
        return <DocumentCard key={doc.id} cardInfor={doc} />
    })

    const notify = () => {
        console.log('Default Notification !')
        toast("Default Notification !");

        toast.success("Success Notification !", {
            position: toast.POSITION.TOP_CENTER
        });

        toast.error("Error Notification !", {
            position: toast.POSITION.TOP_LEFT
        });

        toast.warn("Warning Notification !", {
            position: toast.POSITION.BOTTOM_LEFT
        });

        toast.info("Info Notification !", {
            position: toast.POSITION.BOTTOM_CENTER
        });

        toast("Custom Style Notification with css class!", {
            position: toast.POSITION.BOTTOM_RIGHT,
            className: 'foo-bar'
        });
    };


    return (
        <>
            {/* <ul>
                {docCards}
            </ul> */}
            <Button onClick={notify} text='test'>Notify</Button>;
            <ToastContainer/>
        </>
    )
}
