import { Button, Grid } from "hero-design";
import { useState } from "react";

export default function DocumentDetails({ doc }: { doc: any }) {
    const [editMode, setEditMode] = useState(false);

    const detailsInfo = ['Title', 'Description', 'Price', 'Publish']

    const detailsInfoElements = detailsInfo.map(key => {
        return (
            <Grid.Row>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                   {key} 
                </Grid.Col>
                <Grid.Col span={[12, 12, 12, 12, 12]}>
                    {doc[key.toLowerCase()]}
                </Grid.Col>
            </Grid.Row>
        )
    })

    function save() {

    }

    return (
        <form onSubmit={save}>
            <Grid>
                {detailsInfoElements}
                <Grid.Row>
                    <Button onClick={(e) => setEditMode(true)} disabled={!editMode} text={"Edit"} />
                    <Button type={'submit'} text={"Save"} />
                </Grid.Row>
            </Grid>
        </form>
    )

}
