import { Badge, Grid } from "hero-design"
import { Image } from 'react-bootstrap';
import { useSelector } from "react-redux";
import { Link, NavLink, useHistory } from "react-router-dom";

const styles = {
    navLink: {
        paddingLeft: 13,
        textDecoration: 'none',
        color: 'black'
    },
}

export default function Header() {
    const history = useHistory()
    const userId = useSelector((state: any) => state.user.id)

    return (
        <Grid.Row>
            <Grid.Col span={[8, 8, 8, 8, 8]}>
                <Image
                    src={'/augustus.png'}
                    roundedCircle
                    style={{
                        width: '100px',
                        height: '',
                        paddingLeft: '20px',
                        cursor: 'pointer'
                    }}
                    onClick={(e: any) => {
                        history.push('/dam')
                    }}
                ></Image>
            </Grid.Col>

            <Grid.Col span={[8, 8, 8, 8, 8]}></Grid.Col>
            <Grid.Col span={[8, 8, 8, 8, 8]} style={{}}>
                <NavLink
                    to="/marketplace"
                    activeStyle={{
                        fontWeight: "bold",
                        color: "black"
                    }}
                    style={styles.navLink}
                >
                    Marketplace
                </NavLink>
                <NavLink
                    to="/carts"
                    activeStyle={{
                        fontWeight: "bold",
                        color: "black"
                    }}
                    style={styles.navLink}
                >
                    Orders
                    <Badge.Count content={9}>
                        <div
                            style={{
                                width: '1rem',
                                height: '1rem',
                                display: 'inline-block',
                                verticalAlign: 'middle',
                                margin: 0,
                                padding: 0,
                            }}
                        />
                    </Badge.Count>
                </NavLink>
                <NavLink
                    to={`/users/${userId}`}
                    activeStyle={{
                        fontWeight: "bold",
                        color: "black"
                    }}
                    style={styles.navLink}
                >
                    Account
                </NavLink>
            </Grid.Col>
        </Grid.Row>
    )
}
