import React from 'react';
import renderWithTheme from '../../../testUtils/renderWithTheme';

import Badge from '..';

describe('rendering', () => {
  it('renders badge content', () => {
    const { queryByText } = renderWithTheme(<Badge content={99} />);

    expect(queryByText('99')).toBeInTheDocument();
  });

  it('renders overflow number', () => {
    const { queryByText } = renderWithTheme(<Badge content={100} />);

    expect(queryByText('99+')).toBeInTheDocument();
  });
});
