import theme from '../../../theme';

import { mapColorFromIntent, mapStrokeWidthFromSize } from '../ProgressCircle';

describe('mapColorFromIntent', () => {
  const { primary, danger, success } = theme.colors.progress;

  it.each`
    intent       | expectedColor
    ${'primary'} | ${primary}
    ${'danger'}  | ${danger}
    ${'success'} | ${success}
    ${undefined} | ${primary}
  `(
    'returns $expectedColor color from $intent intent',
    ({ intent, expectedColor }) => {
      expect(mapColorFromIntent(intent, theme)).toBe(expectedColor);
    }
  );
});

describe('mapStrokeWidthFromSize', () => {
  const { circleSmallStroke, circleMediumStroke } = theme.sizes.progress;

  it.each`
    size         | expectedStrokeWidth
    ${'small'}   | ${circleSmallStroke}
    ${'medium'}  | ${circleMediumStroke}
    ${undefined} | ${circleMediumStroke}
  `(
    'returns $expectedColor color from $intent intent',
    ({ size, expectedStrokeWidth }) => {
      expect(mapStrokeWidthFromSize(size, theme)).toBe(expectedStrokeWidth);
    }
  );
});
