import { CSSProperties } from 'react';

export interface CommonProps {
  /**
   * A space-delimited list of class names to pass along to the element.
   */
  className?: string;
  /**
   * Testing id of the element.
   */
  'data-test-id'?: string;
  /**
   * Id of the element.
   */
  id?: string;
  /**
   * Additional inline style to be applied for the most outer element of the component.
   */
  style?: CSSProperties;
}
