interface Sizes {
  large: number;
  medium: number;
  small: number;
  xlarge: number;
  xsmall: number;
  xxlarge: number;
  xxsmall: number;
  xxxlarge: number;
}

const BASE = 8;

const sizes: Sizes = {
  xxsmall: BASE * 0.25, // tiny
  xsmall: BASE * 0.5, //   mini
  small: BASE,
  medium: BASE * 2,
  large: BASE * 3,
  xlarge: BASE * 4, //     big
  xxlarge: BASE * 5, //    huge
  xxxlarge: BASE * 6, //   massive
};

export { sizes };
