interface Space {
  large: number;
  medium: number;
  small: number;
  xlarge: number;
  xsmall: number;
  xxlarge: number;
  xxsmall: number;
  xxxlarge: number;
  xxxxlarge: number;
}

const BASE = 8;

const space: Space = {
  xxsmall: BASE * 0.25,
  xsmall: BASE * 0.5,
  small: BASE,
  medium: BASE * 2,
  large: BASE * 3,
  xlarge: BASE * 4,
  xxlarge: BASE * 5,
  xxxlarge: BASE * 6,
  xxxxlarge: BASE * 7,
};

export { space, Space };
