const breakpoints = ['sm', 'md', 'lg', 'xl'] as const;
type Breakpoint = typeof breakpoints[number];

const getBreakpointValue = (bp: Breakpoint): number => {
  switch (bp) {
    case 'sm':
      return 575;
    case 'md':
      return 768;
    case 'lg':
      return 992;
    case 'xl':
      return 1200;
  }
};

export { breakpoints, Breakpoint, getBreakpointValue };
